function eventNewPlayer(playerName)
    if not data[playerName] then
        data[playerName] = {}
        data[playerName].lang = (tfm.get.room.community == "xx") and "en" or tfm.get.room.community
        data[playerName].objId = getNextObjId()
        data[playerName].colorIndex = 1
        data[playerName].color = getRandomColor()
    end
    data[playerName].answer = nil
    data[playerName].backId = 0
    data[playerName].isAfk = false
    system.bindKeyboard(playerName, 72, true, true)
    data[playerName].voted = 0
    data[playerName].x, data[playerName].tower, data[playerName].isPlaying = 0, 0, false
    if gameStage == 1 then
        tfm.exec.respawnPlayer(playerName)
    end
    setBackground(playerName)
    chooseRoomHost()
    joinButton()
    buttonMenu:setVisible(true, playerName)
    menu.welcome[lang]:setVisible(true, playerName)
    menu.flag[lang]:setVisible(true, playerName)
end

function getRandomColor()
    local index, j = math.random(8), 0
    for i in pairs(colors) do
        j = j + 1
        if j == index then
            return colors[i]
        end
    end
    return colors["red"]
end

