function chooseRoomHost()
    if not hostChosen and countPlayers() == 1 then
        for playerName, _ in pairs(tfm.get.room.playerList) do
            admins[playerName] = true
        end
        hostChosen = true
    end
end

function countPlayersAvailable()
    playersAvailable = 0
    for name, player in pairs(tfm.get.room.playerList) do
        if data[name].isPlaying == true and player.isDead == false then
            playersAvailable = playersAvailable + 1
        end
    end
end

function countPlayers()
    local count = 0
    for _ in pairs(tfm.get.room.playerList) do
        count = count + 1
    end
    return count
end

function checkWinner()
    local alive = 0
    for name, playerData in pairs(tfm.get.room.playerList) do
        if playerData.isDead == false then
            alive = alive + 1
        end
    end
    if alive < 2 then
        local winner, tie
        for name, playerData in pairs(tfm.get.room.playerList) do
            if data[name].isPlaying == true then
                if not winner then
                    winner = name
                else
                    if gameMode == 1 then
                        if data[winner].tower < data[name].tower then
                            winner = name
                            tie = nil
                        elseif data[winner].tower == data[name].tower then
                            if not tie then
                                tie = {}
                                table.insert(tie, winner)
                            end
                            table.insert(tie, name)
                        end
                    elseif gameMode == 2 then
                        if data[winner].tower > data[name].tower then
                            winner = name
                            tie = nil
                        elseif data[winner].tower == data[name].tower then
                            if not tie then
                                tie = {}
                                table.insert(tie, winner)
                            end
                            table.insert(tie, name)
                        end
                    end
                end
            end
        end
        if tie then
            ui.addTextArea(getPopupIndex("topic"), "<p align='center'><font size='40' color='#EDFF03' >"..table.concat(tie, ", ").." "..getLang("player.tie", lang).."</font></p>", nil, 0, 160, 800, 0, 0x181B1B, 0x000000, 0.8, true) 
        elseif winner then
            ui.addTextArea(getPopupIndex("topic"), "<p align='center'><font size='40' color='#EDFF03' >"..winner.." "..getLang("player.won", lang).."</font></p>", nil, 0, 160, 800, 0, 0x181B1B, 0x000000, 0.8, true) 
            tfm.exec.setPlayerScore(winner, tfm.get.room.playerList[winner].score + 1, false)
        end
        timer.countdown = timer.won
        gameStage = 4
        return true
    end
    return false
end
function replanishTopics()
    topicsAvailable = nil
    for j in pairs(question[lang]) do
        if not topicsAvailable then
            topicsAvailable = {} 
        end
        table.insert(topicsAvailable, j)
    end
end

function newRound()
    countPlayersAvailable()
    if not topicsAvailable or #topicsAvailable == 0 then
        replanishTopics()
    end
    if checkWinner() == true then
        return 
    end
    rounds = rounds + 1
    local index, picked = 0, math.random(#topicsAvailable) 
    for name, playerData in pairs(tfm.get.room.playerList) do
        if data[name].isPlaying == true then
            data[name].answer = nil
        end
    end
    topic = question[lang][topicsAvailable[picked]]
    if not topic.answer then
        topic.answer = question["en"][topicsAvailable[picked]].answer
    end
    table.remove(topicsAvailable, picked)
    ui.addTextArea(getPopupIndex("topic"), "<p align='center'><font size='40' color='#EDFF03' >"..topic.question.."</font></p>", nil, 0, 160, 800, 0, 0x181B1B, 0x000000, 0.8, true) 
    showPopup()
end

