        ui.addTextArea(getPopupIndex("vote.count.1"), "<p align='center'><font size='18' color='#E50000' ><a href='event:vote_1'>"..votes[1].."</a></font></p>", nil, 200, 360, 50, 0, 0x181B1B, 0x000000, 0.8, true) 
        ui.addTextArea(getPopupIndex("vote.count.2"), "<p align='center'><font size='18' color='#02A4B2' ><a href='event:vote_2'>"..votes[2].."</a></font></p>", nil, 550, 360, 50, 0, 0x181B1B, 0x000000, 0.8, true)
    else
