function doCountdown()
    if starting then
        return
    end
    timer.countdown = timer.countdown - 1
    ui.addTextArea(getPopupIndex("countdown"), "<p align='center'><font size='28' color='#EDFF03' >"..timer.countdown.."</font></p>", nil, 380, 25, 40, 40, 0x181B1B, 0x000000, 0.8, true) 
    if timer.countdown <= 0 then
        ui.removeTextArea(getPopupIndex("countdown"), nil)
        if gameStage == 1 then
            for name, playerData in pairs(tfm.get.room.playerList) do
                if data[name].isAfk == false then
                    data[name].isPlaying = true
                end
            end
            countPlayersAvailable()
            tfm.exec.addPhysicObject(floor.bottom.id, size.mapLength/2, floor.bottom.y + (20/2), { type = 11, restitution = 0.2, friction = 0.3, width = size.mapLength*2, height = 20, miceCollision = true, dynamic = false, fixedRotation = true, linearDamping = 1000})
            if playersAvailable < 2 then
                timer.countdown = timer.lobby
                freezePlayers(false)
                return
            end
            local regionWidth = size.mapLength / (playersAvailable + 1)
            local playerIndex = 1
            for name, playerData in pairs(tfm.get.room.playerList) do
                if data[name].isPlaying == true and playerData.isDead == false then
                    local x = playerIndex * regionWidth
                    data[name].x = x
                    data[name].tower = 0
                    tfm.exec.movePlayer(name, x, size.baseMap - 30)
                    tfm.exec.freezePlayer(name, true, false)
                    playerIndex = playerIndex + 1
                else
                    tfm.exec.killPlayer(name)
                end
            end
            gameMode = votes[1] > votes[2] and 1 or votes[2] > votes[1] and 2 or math.random(2)
            if gameMode == 1 then
                floor.kill.y = size.baseMap + 20
                ui.addTextArea(getPopupIndex("game.mode"), "<p align='center'><font size='28' color='#E50000' >"..getLang("game.text.big", lang).."</font></p>", nil, 200, 110, 400, 0, 0x181B1B, 0x000000, 0.8, true) 
            elseif gameMode == 2 then
                floor.kill.y = size.lobby + 125
                ui.addTextArea(getPopupIndex("game.mode"), "<p align='center'><font size='28' color='#02A4B2' >"..getLang("game.text.small", lang).."</font></p>", nil, 200, 110, 400, 0, 0x181B1B, 0x000000, 0.8, true) 
            end
            moveKillFloor()
            gameStage = 2
            joinButton()
        elseif gameStage == 2 then
            timer.countdown = timer.acid
            for name, player in pairs(tfm.get.room.playerList) do
                if gameMode == 2 and not data[name].answer and data[name].isPlaying == true and rounds >=1 and player.isDead == false then
                    data[name].answer = blankAnswer
                    raiseTower(name)
                end
                if data[name].answer then
                    for i = 0, string.len(data[name].answer)-1 do
                        ui.addTextArea(getPopupIndex(name..".tower."..data[name].tower - i), "<p align='center'><font size='18' color='#EDFF03' >"..data[name].answer:sub(i+1,i+1):upper().."</font></p>", nil, data[name].x - (size.towerLength), ((size.baseMap - ((data[name].tower - i)* size.towerHeight)) - (size.towerHeight /2))-2, 40, 40, 0x181B1B, 0x000000, 0, false) 
                    end
                end
            end
            gameStage = 3
        elseif gameStage == 3 then
            timer.countdown = timer.answer
            gameStage = 2
            newRound()
        elseif gameStage == 4 then
            reset()
        end
    elseif timer.countdown == 3 and gameStage == 1 then
        freezePlayers(true)
    end
end

function freezePlayers(freeze)
    for playerName, playerData in pairs(tfm.get.room.playerList) do
        tfm.exec.freezePlayer(playerName, freeze, false)
    end
end

function getNextObjId()
    objId = objId + 1
    return objId - 1
end

function eventLoop(currentTime, timeRemaining)
    if starting == true then
        return
    end
    timer.secondsPassed = timer.secondsPassed + 0.5
    if timer.secondsPassed == math.floor(timer.secondsPassed) then
        --joinButton()
        doCountdown()
    end
    if gameStage == 3 then
        local displacement = 0
        if rounds >= 2 and rounds <= 4 then
            displacement = 10
        elseif rounds >= 5 and rounds <= 7 then
            displacement = 20
        elseif rounds >= 8 and rounds <= 10 then
            displacement = 40
        elseif rounds >= 11 then
            displacement = 100
        end
        displacement = gameMode == 2 and displacement * -1 or displacement 
        if rounds >= 2 then
            floor.kill.y = floor.kill.y - displacement
            moveKillFloor()
        end
    end
end

function moveKillFloor()
    tfm.exec.addPhysicObject(floor.kill.id, size.mapLength/2, floor.kill.y + (500/2), { type = 19, restitution = 0.2, friction = 0.3, width = size.mapLength*2, height = 500, miceCollision = true, dynamic = false, fixedRotation = true, linearDamping = 1000})
end

function eventTextAreaCallback(id, playerName, event)
    local args = {}
    for s in event:gmatch("([^_]+)") do
        table.insert(args, s)
    end
    if args[1] == "color" then
        if args[2] == "close" then
            menu.color[lang]:setVisible(false, playerName)
        else
            data[playerName].color =colors[args[2]]
        end
    elseif args[1] == "menu" then
        menu.color[lang]:setVisible(true, playerName)
    elseif args[1] == "welcome" then
        if args[2] == "close" then
            menu.welcome[args[3]]:setVisible(false, playerName)
        end
    elseif args[1] == "vote" then
        voteMode(playerName,tonumber(args[2]))
    elseif args[1] == "lang" and admins[playerName] then
        menu.flag[lang]:setVisible(false)
        setLang(args[2])
        menu.flag[lang]:setVisible(true)
        replanishTopics()
        reset()
    end
end

function eventChatCommand(playerName, cmd)
    local args = {}
    for s in cmd:gmatch("[%S]+") do
        table.insert(args, s)
    end
    if args[1] == "admins" then
        local adminList = {}
        for adminName, _ in pairs(admins) do
            table.insert(adminList, adminName)
        end
        tfm.exec.chatMessage("Admins: " ..table.concat(adminList, ", "), playerName)
    elseif args[1] == "reset" and admins[playerName] then
        reset()
    end
end

function voteMode(playerName, mode)
    if data[playerName].voted == mode then
        return
    end
    if data[playerName].voted ~= 0 then
        votes[data[playerName].voted] = votes[data[playerName].voted] - 1
    end
    votes[mode] = votes[mode] + 1
    data[playerName].voted = mode
    joinButton()
end

