function eventPopupAnswer(id, playerName, answer)
    if id == getPopupIndex("topic.answer") and gameStage == 2 then
        if not topic or data[playerName].answer then
            return
        end
        answer = stripChars(answer:lower():gsub("%s+", ""))
        local valid = false
        for i, j in pairs(topic.answer) do
            if stripChars(j:lower():gsub("%s+", "")) == answer then
                valid = true
                break
            end
        end
        if valid == false then
            showPopup(playerName)
        else
            data[playerName].answer = answer
            raiseTower(playerName)
        end
    end
end

function raiseTower(playerName)
    for i = 1, string.len(data[playerName].answer) do
        table.insert(tower.imgIds, tfm.exec.addImage(data[playerName].color[data[playerName].colorIndex]..'.png', '!1', data[playerName].x- (size.towerLength /2), (size.baseMap - ((data[playerName].tower + i)* size.towerHeight)) - (size.towerHeight /2), nil, size.towerLength, size.towerLength, 0, 1, 0, 0, false))
        ui.addTextArea(getPopupIndex(playerName..".tower."..data[playerName].tower + i), "<p align='center'><font size='18' color='#EDFF03' >?</font></p>", nil, data[playerName].x - (size.towerLength), (((size.baseMap) - ((data[playerName].tower + i)* size.towerHeight)) - (size.towerHeight /2))-2, 40, 40, 0x181B1B, 0x000000, 0, false) 
        data[playerName].colorIndex = data[playerName].colorIndex == 1 and 2 or 1
    end
    data[playerName].tower = data[playerName].tower + string.len(data[playerName].answer)
    tower.txtIds[playerName] = data[playerName].tower
    tfm.exec.addPhysicObject(data[playerName].objId, data[playerName].x, size.baseMap - (data[playerName].tower * size.towerHeight), { type = 0, restitution = 0.2, friction = 0.3, width = size.towerLength, height = size.towerHeight, miceCollision = true, dynamic = false, fixedRotation = true, linearDamping = 1000})
    tfm.exec.movePlayer(playerName, data[playerName].x, size.baseMap - (data[playerName].tower * size.towerHeight) - 20)
end

function showPopup(playerName)
    local players = {}
    if playerName then
        players[playerName] = true
    else
        players = tfm.get.room.playerList
    end
    for name in pairs(players) do
        if data[name].isPlaying == true and not data[name].answer and tfm.get.room.playerList[name].isDead == false then
            ui.addPopup(getPopupIndex("topic.answer"), 2, "", name, 300, 310, 200, true)
        end
    end
end

